<script src="https://cdnjs.cloudflare.com/ajax/libs/aos/2.3.4/aos.js"></script>
<script>
// Initialize AOS (Animate On Scroll)
AOS.init({
  duration: 800,
  easing: 'ease-in-out-cubic',
  once: false,
  mirror: true,
  offset: 100
});

// Counter animation for stats
function animateCounter(element, target, duration = 2000) {
  const start = 0;
  const startTime = Date.now();
  
  function update() {
    const elapsed = Date.now() - startTime;
    const progress = Math.min(elapsed / duration, 1);
    const current = Math.floor(start + (target - start) * easeOutQuad(progress));
    element.textContent = current.toLocaleString();
    
    if (progress < 1) {
      requestAnimationFrame(update);
    } else {
      element.textContent = target.toLocaleString();
    }
  }
  
  function easeOutQuad(t) {
    return 1 - (1 - t) * (1 - t);
  }
  
  update();
}

// Trigger counters when stats section is visible
const observerOptions = {
  threshold: 0.5,
  rootMargin: '0px'
};

const counterObserver = new IntersectionObserver((entries) => {
  entries.forEach(entry => {
    if (entry.isIntersecting) {
      const statNumbers = entry.target.querySelectorAll('.stat-number');
      const targets = [15, 20000, 99];
      statNumbers.forEach((num, index) => {
        animateCounter(num, targets[index]);
      });
      counterObserver.unobserve(entry.target);
    }
  });
}, observerOptions);

const heroStats = document.querySelector('.hero-stats');
if (heroStats) {
  counterObserver.observe(heroStats);
}

// Header scroll effect
const header = document.getElementById('header');
window.addEventListener('scroll', () => {
  if (window.scrollY > 50) {
    header.classList.add('scrolled');
  } else {
    header.classList.remove('scrolled');
  }
});

// Mobile menu toggle
const mobileMenuBtn = document.getElementById('mobileMenuBtn');
const navLinks = document.getElementById('navLinks');

mobileMenuBtn.addEventListener('click', () => {
  navLinks.classList.toggle('active');
  const icon = mobileMenuBtn.querySelector('i');
  if (navLinks.classList.contains('active')) {
    icon.classList.remove('fa-bars');
    icon.classList.add('fa-times');
    document.body.style.overflow = 'hidden';
  } else {
    icon.classList.remove('fa-times');
    icon.classList.add('fa-bars');
    document.body.style.overflow = '';
  }
});

document.querySelectorAll('.nav-links a').forEach(link => {
  link.addEventListener('click', () => {
    navLinks.classList.remove('active');
    mobileMenuBtn.querySelector('i').classList.remove('fa-times');
    mobileMenuBtn.querySelector('i').classList.add('fa-bars');
    document.body.style.overflow = '';
  });
});

// Modal functionality
const dashboardModal = document.getElementById('dashboardModal');
const closeModal = document.getElementById('closeModal');
const closeModalBtn = document.getElementById('closeModalBtn');

// Use SVG dashboard as modal trigger
document.querySelector('.dashboard-image').addEventListener('click', () => {
  dashboardModal.classList.add('active');
  document.body.style.overflow = 'hidden';
});

const closeModalFunc = () => {
  dashboardModal.classList.remove('active');
  document.body.style.overflow = '';
};

closeModal.addEventListener('click', closeModalFunc);
closeModalBtn.addEventListener('click', closeModalFunc);

dashboardModal.addEventListener('click', (e) => {
  if (e.target === dashboardModal) {
    closeModalFunc();
  }
});

document.addEventListener('keydown', (e) => {
  if (e.key === 'Escape' && dashboardModal.classList.contains('active')) {
    closeModalFunc();
  }
});

// Refresh AOS on dynamic content
window.addEventListener('load', () => {
  AOS.refresh();
});
</script>
</body>
</html>
